import React from 'react';
import FlowData from '../Data/FlowData';
import PageWrapper from './PageWrapper';
import TableComponent from './TableComponent';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import TextField from '@material-ui/core/TextField';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import Divider from '@material-ui/core/Divider';

class SingleFlowComponent extends React.Component {
  constructor (props){
    super(props);
    this.state = {time: "", next: this.props.rowid+1, row: this.props.rowid, blocked: false, reason: ""};
  }

  next = () => {
    this.setState({time: (new Date()).getTime()}, ()=>{
      this.props.logs.push(this.state);
      console.log(this.props.logs);
      this.setState({
        row: this.state.row +1,
        next: this.state.next +1,
        reason: ""
      });
    })

  }

  handleBlockSwitch = () =>{
    this.setState({
      blocked: !this.state.blocked
    })
  }

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };

  render(){
    return(
      <PageWrapper>
        <div className="page-contents">
          <div className="page-sub-title-div">
            <h2 className="page-sub-title">Flow Data Phase 1</h2>
          </div>
          <div className="flow-detail-pane">
          <h4>Flow Details</h4>
            <ul>
              <li>{FlowData.rows[1]}: {FlowData.data[this.props.rowid][1]}</li>
              <li>{FlowData.rows[2]}: {FlowData.data[this.props.rowid][2]}</li>
              <li>{FlowData.rows[3]}: {FlowData.data[this.props.rowid][3]}</li>
              <li>{FlowData.rows[4]}: {FlowData.data[this.props.rowid][4]}</li>
              <li>{FlowData.rows[5]}: {FlowData.data[this.props.rowid][5]}</li>
              <li>{FlowData.rows[6]}: {FlowData.data[this.props.rowid][6]}</li>
              <li>{FlowData.rows[7]}: {FlowData.data[this.props.rowid][7]}</li>
              <li>{FlowData.rows[8]}: {FlowData.data[this.props.rowid][8]}</li>
              <li>{FlowData.rows[9]}: {FlowData.data[this.props.rowid][9]}</li>
            </ul>
          </div>
          <div className="flow-gui-pane">
          <Divider />
            <h4>GUI Information</h4>
            <TableComponent rowheaders={["Clicks", "Key Strokes"]} rows={["User Action", "0-5 Seconds", "5-15 Seconds", "15-60 Seconds", "1-3 Minutes", "3-5 Minutes"]} data={[JSON.parse(FlowData.data[this.state.row][10]), JSON.parse(FlowData.data[this.state.row][11])]} logs={this.props.logs} options={false}/>
            <div>{FlowData.data[this.props.rowid][12]}</div>

          </div>
          <Divider />
          <div className="policy-card">
            <FormGroup>
              <FormControlLabel
                control={
                  <Switch checked={!this.state.blocked} onChange={this.handleBlockSwitch} aria-label="BlockSwitch" />
                }
                label={this.state.blocked ? 'Block' : 'Allow'}
              />
            </FormGroup>
            <TextField
            id="outlined-multiline-flexible"
            value={this.state.reason}
            onChange={this.handleChange('reason')}
            label="Reason for decision"
            multiline
            fullWidth
            margin="normal"
            variant="outlined"
            />
          </div>

          <Link to={"/flow"+this.state.next}><Button variant="outlined" onClick={this.next}>Next</Button></Link>
        </div>
      </PageWrapper>
    )
  }
}

export default SingleFlowComponent;
